<template>
  <section>
    <a href="#collapseNewPost"
       class="btn btn-light"
       style="width: 100%"
       data-toggle="collapse">
      <img alt="add post"
           width="25rem"
           src="data:image/svg+xml;base64," />
      Добавление нового поста
    </a>
    <div class="collapse" id="collapseNewPost">
      <form class="form-group" action='' method="POST" @submit.prevent="submitHandler">
        <label style="width: 100%">
          <input class="form-control"
                 type="text"
                 placeholder="Title"
                 v-model="title"
          />
        </label>
        <label style="width: 100%">
          <textarea class="form-control"
                    type="text"
                    placeholder="Введите текст..."
                    v-model="post"
          />
        </label>
        <label class="control-group">
          <button class="btn btn-light">
            <img alt="add post"
                 width="25rem"
                 src="data:image/svg+xml;base64," />
            Опубликовать
          </button>
        </label>
      </form>
    </div>
  </section>
</template>

<script>
import {email, minLength, required} from "vuelidate/lib/validators";

export default {
name: "AddPost_update",
  data: () => ({
    title: '',
    post: '',
  }),
  validations: {
    title: {required},
    post: {},
  },
  methods: {
    async submitHandler (){
      try{
        if(this.$v.$invalid){
          this.$v.$touch()
          return
        }
        const formData ={
          title: this.title,
          post: this.post
        }
        await this.$store.dispatch('addPostsUpdate', formData)
        this.$toast.success('Запись успешно добавлена!');
      }catch (e) {
        this.$toast.error('Ошибка добавления записи!');
      }
    },
  }
}
</script>

<style scoped>
form{
  padding: 2rem;
}
</style>
